import React, { useState } from 'react';
import '../styles/style.scss';
import Logo from '../assets/logo.svg';

function Home() {
  // 定義 state 來存儲搜尋欄位的值
  const [searchTerm, setSearchTerm] = useState('');

  // 定義靜態資料列表（例如實驗名稱與描述）
  const experiments = [
    { id: 1, name: 'Experiment 1: Chemical Reaction', description: 'Study of chemical reactions and properties.' },
    { id: 2, name: 'Experiment 2: Physics Simulation', description: 'Simulation of various physics phenomena.' },
    { id: 3, name: 'Experiment 3: Biology Study', description: 'Observation and study of biological organisms.' },
    { id: 4, name: 'Experiment 4: Quantum Mechanics', description: 'Exploration of quantum mechanical systems.' },
    { id: 5, name: 'Experiment 5: AI Model Training', description: 'Training of machine learning models.' },
    { id: 6, name: 'Experiment 6: Robotics Programming', description: 'Programming and controlling robots.' }
  ];

  // 過濾資料列表
  const filteredList = experiments.filter((experiment) =>
    experiment.name.toLowerCase().includes(searchTerm.toLowerCase())
  );

  // 處理搜尋輸入變更
  const handleInputChange = (e) => {
    setSearchTerm(e.target.value);
  };

  return (
    <div className='container'>
      <nav className='navbar'>
        <img src={Logo} alt="" />
      </nav>
      <div className='searching_area'>
        <p className='searching_title'>Experiments</p>
        <div className='searching_result'>
          {/* 搜尋欄位 */}
          <input
            type="text"
            placeholder="Type keywords to filter database names"
            value={searchTerm}
            onChange={handleInputChange}
            className='input_style'
          />

          {/* 使用 Table 來顯示過濾後的列表 */}
          <table className='search_table'>
            <thead>
              <tr>
                <th>ID</th>
                <th>Experiment Name</th>
                <th>Description</th>
              </tr>
            </thead>
            <tbody>
              {filteredList.length > 0 ? (
                filteredList.map((experiment) => (
                  <tr key={experiment.id}>
                    <td>{experiment.id}</td>
                    <td>{experiment.name}</td>
                    <td>{experiment.description}</td>
                  </tr>
                ))
              ) : (
                <tr>
                  <td colSpan="3">找不到符合條件的項目</td>
                </tr>
              )}
            </tbody>
          </table>
        </div>
      </div>
    </div>
  );
}

export default Home;
